class GetRandomString{
	def log;
	def context;
	def testRunner;

	def GetRandomString(log,context,testRunner){
		this.log = log;
		this.context = context;
		this.testRunner=testRunner;
	}

	def sayHello(String hell){
		log.info "Printing ..........."+hell
	}

}
context.setProperty("GetRandomString", new GetRandomString(log,context,testRunner))

